import React, { useState } from 'react';
import Banner from '../components/Banner';
import userIcon from '../img/ic_user.svg';
import emailIcon from '../img/ic_email.svg';

function Contactuspage() {
  const [formData, setFormData] = useState({ name: '', email: '', message: '' });

  const handleChange = (e) => {
    setFormData(prev => ({ ...prev, [e.target.name]: e.target.value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await fetch('http://localhost:5000/api/contact', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(formData)
      });
      const data = await res.json();
      if (data.success) {
        alert('Message sent successfully!');
        setFormData({ name: '', email: '', message: '' });
      } else {
        alert('Failed to send message.');
      }
    } catch (err) {
      alert('Error submitting form.');
      console.error(err);
    }
  };

  return (
    <div>
      <Banner apiEndpoint="http://localhost:5000/api/banner/contact" height="400px" />
      <section className="contact-us container py-5">
        <h2 className="text-center mb-2">Contact Us</h2>
        <div className="blue-line mx-auto mb-4"></div>

        <form onSubmit={handleSubmit}>
          <div className="row justify-content-center gx-3 gy-3 mb-3">
            <div className="col-12 col-md-6 col-lg-4 d-flex justify-content-center">
              <div className="input-tab w-100">
                <input name="name" type="text" placeholder="Your Name" value={formData.name} onChange={handleChange} />
                <img src={userIcon} alt="Icon" />
              </div>
            </div>
            <div className="col-12 col-md-6 col-lg-4 d-flex justify-content-center">
              <div className="input-tab w-100">
                <input name="email" type="email" placeholder="Your Email" value={formData.email} onChange={handleChange} />
                <img src={emailIcon} alt="Icon" />
              </div>
            </div>
          </div>

          <div className="row justify-content-center mb-4">
            <div className="col-12 col-md-10 col-lg-8">
              <div className="message-box">
                <textarea name="message" placeholder="Your Message" value={formData.message} onChange={handleChange}></textarea>
              </div>
            </div>
          </div>

          <div className="text-center mt-4">
            <button className="submit-btn" type="submit">Submit</button>
          </div>
        </form>
      </section>
    </div>
  );
}

export default Contactuspage;
