// src/components/Banner.js
import React, { useEffect, useState } from 'react';

const Banner = () => {
  const [imageUrl, setImageUrl] = useState('');

  useEffect(() => {
    // This fetches banner image data from your backend API
    fetch('http://localhost:5000/api/banner')  // Adjust this route to your actual API
      .then(res => res.json())
      .then(data => {
        setImageUrl(data.imgUrl); // imgUrl should be the full backend URL to image
      })
      .catch(err => {
        console.error('Failed to load banner image:', err);
      });
  }, []);

  return (
    <section
      className="banner container-fluid"
      style={{
        background: `url(${imageUrl}) center center/cover no-repeat`,
        position: 'relative',
        height: '532px',
        display: 'flex',
        flexDirection: 'column',
        justifyContent: 'center',
        alignItems: 'center',
        textAlign: 'center',
        color: 'white',
        padding: '0 15px'
      }}
    >
      <h1>
        Lorem ipsum <span className="highlight">dolor sit</span> amet consectetur.
      </h1>
      <p>
        Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.
      </p>
    </section>
  );
};

export default Banner;
