// components/Banner.js
import React, { useEffect, useState } from 'react';

const Banner = ({ apiEndpoint }) => {
  const [imageUrl, setImageUrl] = useState('');

  useEffect(() => {
    fetch(apiEndpoint)
      .then(res => res.json())
      .then(data => setImageUrl(data.imgUrl))
      .catch(err => console.error('Failed to load banner image:', err));
  }, [apiEndpoint]);

  return (
    <section
      className="banner container-fluid"
      style={{
        background: `url(${imageUrl}) center center/cover no-repeat`,
        height: '532px', // adjust this via props if needed
        display: 'flex',
        flexDirection: 'column',
        justifyContent: 'center',
        alignItems: 'center',
        textAlign: 'center',
        color: 'white',
        padding: '0 15px'
      }}
    >
      <h1>
        Lorem ipsum <span className="highlight">dolor sit</span> amet consectetur.
      </h1>
      <p>
        Lorem ipsum dolor sit amet, consectetur adipiscing elit.
      </p>
    </section>
  );
};

export default Banner;
